import {useLocation, useNavigate} from "react-router-dom";
import {Field, Form, Formik} from "formik";

export default function Login() {
    const navigate = useNavigate()
    const mess = useLocation();
    const handleLogin = (values) => {
        if (values.username === 'admin' && values.password ==='1') {
            navigate('admin')
        } else if (values.username === 'user' && values.password ==='1') {
            navigate('user', {state: values.username})
        } else {
            navigate('', {state: 'Sai tài khoản hoặc mật khẩu'})
        }
    }
    return (
        <>
            {mess && <h1>{mess.state}</h1>}
            <Formik initialValues={
                {
                    username: '',
                    password: ''
                }
            } onSubmit={(values) => {
                handleLogin(values)
            }}
            >
                <Form>
                <Field type="text" name={'username'}/>
                <Field type="text" name={'password'}/>
                <button className={'btn btn-primary'}>Login</button>
                </Form>
            </Formik>
        </>
    )
}
