import {Link, useNavigate} from "react-router-dom";
import {useEffect, useState} from "react";
import {toast, useToast} from "react-toastify";


export default function Login() {
    const[username, usernameUpdate] = useState('')
    const[password, passwordUpdate] = useState('')
    const ProceedLogin=(e)=>{
        e.preventDefault();
        if (validate()) {
            console.log('proceed')
        }
    }
    const validate = () => {
        let result = true;
        if (username === '' || username === null) {
            result = false;
            useToast.warning('Please Enter Username');
        }
        if (password === '' || password === null) {
            result = false;
            useToast.warning('Please Enter Password');
        }
        return result;
    }


    return (
        <>
            <div className={'row'}>
                <div className={'offset-lg-3 col-lg-6'}>
                    <form onSubmit={ProceedLogin} className={'container'}>
                        <div className={'card'}>
                            <div className={'card-header'}>
                                <h2>User login</h2>
                            </div>
                            <div className={'card-body'}>
                                <div className={'form-group'}>
                                    <label>Username<span style={{color: "red"}}>*</span></label>
                                    <input value={username} onChange={e => usernameUpdate(e.target.value)} className={'form-control'}/>
                                </div>
                                <div className={'form-group'}>
                                    <label>Password<span style={{color: "red"}}>*</span></label>
                                    <input value={password} onChange={e => passwordUpdate(e.target.value)} className={'form-control'}/>
                                </div>
                            </div>
                            <div className={'card-footer'}>
                                <button type={"submit"} className={'btn btn-outline-primary'}>Login</button> |
                                <Link className={'btn btn-outline-success'} style={{float: 'right'}} to={'/register'}>New user</Link>
                            </div>

                        </div>
                    </form>
                </div>

            </div>
        </>
    )
}






