import {Link, useLocation, useNavigate} from "react-router-dom";
import {Field, Form, Formik} from "formik";

export default function Login() {
    const navigate = useNavigate()
    const mess = useLocation();
    const handleLogin = (values) => {
        if (values.username === 'admin' && values.password ==='1') {
            navigate('admin')
        } else if (values.username === 'user' && values.password ==='1') {
            navigate('home', {state: values.username})
        } else {
            navigate('', {state: 'Sai tài khoản hoặc mật khẩu'})
        }
    }
    return (
        <>
            {mess && <h1>{mess.state}</h1>}
            <Formik initialValues={
                {
                    username: '',
                    password: ''
                }
            } onSubmit={(values) => {
                handleLogin(values)
            }}
            >
                <div className="limiter">
                    <div className="container-login100">
                        <div className="wrap-login100">
                            <Form className="login100-form validate-form">
					<span className="login100-form-title p-b-26">
						Welcome
					</span>
                                <span className="login100-form-title p-b-48">
						<i className="zmdi zmdi-font"></i>
					</span>

                                <div className="wrap-input100 validate-input" data-validate="Valid email is: a@b.c">
                                    <Field className="input100" type="text" name="username"/>
                                        <span className="focus-input100" data-placeholder="Email"></span>
                                </div>

                                <div className="wrap-input100 validate-input" data-validate="Enter password">
						<span className="btn-show-pass">
							<i className="zmdi zmdi-eye"></i>
						</span>
                                    <Field className="input100" type="password" name="password"/>
                                        <span className="focus-input100" data-placeholder="Password"></span>
                                </div>

                                <div className="container-login100-form-btn">
                                    <div className="wrap-login100-form-btn">
                                        <div className="login100-form-bgbtn"></div>
                                        <button className="login100-form-btn">
                                            Login
                                        </button>
                                    </div>
                                </div>

                                <div className="text-center p-t-115">
						<span className="txt1">
							Don’t have an account?
						</span>
                                    <Link to={'/'} className="txt2" href="#">
                                        Sign Up
                                    </Link>
                                </div>
                            </Form>
                        </div>
                    </div>
                </div>
            </Formik>
        </>
    )
}
